  function toggle(){

  }

  const  addingcards = carddata.datas.map(card => {
    return(
     <Card
      key = {card.id}   
      id= {card.id}
      name= {card.name}
      value= {card.value}
      on= {card.on}
      image = {card.image}
      clickhandler = {toggle}
     />)    
  })

  return (
    <div className="leftHeaderItems">
        {addingcards}
    </div>
  );

};  

export default Row;
