  function CardKnow(){

    const found = cardarrays.find(card => card.id === props.id);
    // console.log(index);
    console.log(found);    
    };

    function AddingReading(){
        UpdateMyArray(readingarray => {
            return [...readingarray, found]
        })
    }


  return (
    <div className="card" onClick={() => {
          CardKnow();
          AddingReading();
        }}> 

    </div>
  );
}

export default Card;
